CSVRecord nextRecord() throws IOException {
  CSVRecord result=null;
  this.record.clear();
  StringBuilder sb=null;
  do {
    this.reusableToken.reset();
    this.lexer.nextToken(this.reusableToken);
switch (this.reusableToken.type) {
case TOKEN:      this.addRecordValue();
    break;
case EORECORD:  this.addRecordValue();
break;
case EOF:if (this.reusableToken.isReady) {
this.addRecordValue();
}
break;
case INVALID:throw new IOException("(line " + this.getCurrentLineNumber() + ") invalid parse sequence");
case COMMENT:if (sb == null) {
sb=new StringBuilder();
}
 else {
sb.append(Constants.LF);
}
sb.append(this.reusableToken.content);
this.reusableToken.type=TOKEN;
break;
default:throw new IllegalStateException("Unexpected Token type: " + this.reusableToken.type);
}
}
 while (this.reusableToken.type == TOKEN);
if (!this.record.isEmpty()) {
this.recordNumber++;
